function minWindow(s: string, t: string): string {
	const T: Record<string, any> = {}; // to count the t values
	const w: Record<string, any> = {}; // to count the t values in the wdow

	// fill the T and get the n count
	for (let i = 0; i < t.length; i++) {
		T[t[i]] = (T[t[i]] || 0) + 1;
	}

	let res: [number, number] = [-1, -1]; // wdow index for result
	let resL = Infinity; // result length - Infinity so that everything is less than it

	let n = Object.keys(T).length; // how many we n
	let h = 0; // how many we h

	let l = 0;
	let r = 0;
	let c = "";

	// create r pointer and iterate over string to fill h values
	while (r < s.length) {
		c = s[r];
		w[c] = (w?.[c] || 0) + 1;

		if (t.includes(c) && w[c] === T[c]) {
			h += 1;
		}

		while (h === n && l < s.length) {
			// calculate wdow length and check if it is less than cur resL
			if (r - l + 1 < resL) {
				res = [l, r];
				resL = r - l + 1;
			}

			c = s[l];
			w[c] = (w?.[c] || 0) - 1;

			if (t.includes(c) && w[c] < T[c]) h -= 1;
			l++;
		}

		r++;
	}

	return resL !== Infinity ? s.substring(res[0], res[1] + 1) : "";
}

console.log({ answer: minWindow("ADOBECODEBANC", "ABC") });
console.log({ answer: minWindow("a", "a") });
